import React, { useState } from 'react';
import { Form, Button, Container, Row, Col, Card } from 'react-bootstrap';
import TableForm from './TableForm';

const ReportForm = () => {
  const departments = [
    "custservice",
    "domesticops",
    "common",
    "domesticprod",
    "domnetint",
    "finance",
    "intlops",
    "intlprod",
    "it",
    "prodtech",
    "sales"
  ];

  const [formData, setFormData] = useState({
    ReportName: {
      reportName: '',
      department: '',
      tables: [],
      powerBI: {
        refresh: false,
        refreshTime: ''
      },
      alertEmails: []
    },
    Alert_E_Mail: [],
    PowerBI_Refresh: {
      DatasetID: '',
      GroupdID: ''
    }
  });

  const [newEmail, setNewEmail] = useState('');

  const handleDepartmentChange = (e) => {
    setFormData({
      ...formData,
      ReportName: {
        ...formData.ReportName,
        department: e.target.value
      }
    });
  };

  const handleReportNameChange = (e) => {
    setFormData({
      ...formData,
      ReportName: {
        ...formData.ReportName,
        reportName: e.target.value
      }
    });
  };

  const handleReportDescriptionChange = (e) => {
    setFormData({
      ...formData,
      ReportName: {
        ...formData.ReportName,
        reportDescription: e.target.value
      }
    });
  };

  const handleFrequencyChange = (e) => {
    setFormData({
      ...formData,
      ReportName: {
        ...formData.ReportName,
        frequency: e.target.value
      }
    });
  };

  const handlePowerBIChange = (field) => (e) => {
    setFormData({
      ...formData,
      PowerBI_Refresh: {
        ...formData.PowerBI_Refresh,
        [field]: e.target.value
      }
    });
  };

  const handleAddTable = () => {
    setFormData({
      ...formData,
      ReportName: {
        ...formData.ReportName,
        tables: [...formData.ReportName.tables, {
          tablename: '',
          query: [{
            queryType: 'SQL',
            QueryName: '',
            QueryOrder: '',
            QueryFileName: '',
            queryParameter: {}
          }],
          LoadType: 'FULL',
          WriteMode: 'APPEND',
          IsView: '0',
          View_Type: 'standalone'
        }]
      }
    });
  };

  const handleAddEmail = () => {
    if (newEmail && !formData.Alert_E_Mail.includes(newEmail)) {
      setFormData({
        ...formData,
        Alert_E_Mail: [...formData.Alert_E_Mail, newEmail]
      });
      setNewEmail('');
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    
    // Create the JSON string
    const jsonString = JSON.stringify(formData, null, 2);
    
    // Create a blob with the JSON data
    const blob = new Blob([jsonString], { type: 'application/json' });
    
    // Create a URL for the blob
    const url = URL.createObjectURL(blob);
    
    // Create a temporary link element
    const link = document.createElement('a');
    link.href = url;
    link.download = `${formData.ReportName.reportName}.json`;
    
    // Append the link to the document
    document.body.appendChild(link);
    
    // Trigger the download
    link.click();
    
    // Clean up
    document.body.removeChild(link);
    URL.revokeObjectURL(url);
  };

  return (
    <Container className="mt-4">
      <div className="dhl-form-container">
        <h2 className="text-center mb-4">Report Configuration</h2>
        <Form onSubmit={handleSubmit}>
          <Card className="mb-4">
            <Card.Body>
              <h4 className="dhl-section-title">Basic Information</h4>
              <Row className="mb-3">
                <Col md={6}>
                  <Form.Group>
                    <Form.Label className="fw-bold">Department</Form.Label>
                    <Form.Select
                      value={formData.ReportName.department}
                      onChange={handleDepartmentChange}
                      required
                      className="dhl-select"
                    >
                      <option value="">Select Department</option>
                      {departments.map(dept => (
                        <option key={dept} value={dept}>{dept}</option>
                      ))}
                    </Form.Select>
                  </Form.Group>
                </Col>
                <Col md={6}>
                  <Form.Group>
                    <Form.Label>Report Name</Form.Label>
                    <Form.Control
                      type="text"
                      value={formData.ReportName.reportName}
                      onChange={handleReportNameChange}
                      required
                    />
                  </Form.Group>
                </Col>
              </Row>
            </Card.Body>
          </Card>

          <div className="dhl-form-section">
            <h4>Tables</h4>
            {formData.ReportName.tables.map((table, index) => (
              <TableForm
                key={index}
                table={table}
                index={index}
                formData={formData}
                setFormData={setFormData}
              />
            ))}
            <Button variant="secondary" onClick={handleAddTable} className="mt-2 dhl-button">
              Add Table
            </Button>
          </div>

          <div className="dhl-form-section">
            <h4>PowerBI Refresh Settings</h4>
            <Row>
              <Col>
                <Form.Group>
                  <Form.Label>Dataset ID</Form.Label>
                  <Form.Control
                    type="text"
                    value={formData.PowerBI_Refresh.DatasetID}
                    onChange={handlePowerBIChange('DatasetID')}
                    className="dhl-input"
                  />
                </Form.Group>
              </Col>
              <Col>
                <Form.Group>
                  <Form.Label>Group ID</Form.Label>
                  <Form.Control
                    type="text"
                    value={formData.PowerBI_Refresh.GroupdID}
                    onChange={handlePowerBIChange('GroupdID')}
                    className="dhl-input"
                  />
                </Form.Group>
              </Col>
            </Row>
          </div>

          <div className="dhl-form-section">
            <h4>Alert Emails</h4>
            <Row className="mb-2">
              <Col>
                <Form.Control
                  type="email"
                  value={newEmail}
                  onChange={(e) => setNewEmail(e.target.value)}
                  placeholder="Enter email"
                  className="dhl-input"
                />
              </Col>
              <Col xs="auto">
                <Button variant="secondary" onClick={handleAddEmail} className="dhl-button">
                  Add Email
                </Button>
              </Col>
            </Row>
            <div className="email-list">
              {formData.Alert_E_Mail.map((email, index) => (
                <div key={index} className="email-item">
                  <span>{email}</span>
                  <Button
                    variant="link"
                    className="text-danger p-0"
                    onClick={() => {
                      const newEmails = formData.Alert_E_Mail.filter((_, i) => i !== index);
                      setFormData({ ...formData, Alert_E_Mail: newEmails });
                    }}
                  >
                    Remove
                  </Button>
                </div>
              ))}
            </div>
          </div>

          <div className="text-center mt-4">
            <Button variant="primary" type="submit" className="dhl-button dhl-button-primary">
              Generate JSON
            </Button>
          </div>
        </Form>
      </div>
    </Container>
  );
};

export default ReportForm;
