import React, { useState } from 'react';
import { Form, Button, Container, Row, Col } from 'react-bootstrap';
import TableForm from './TableForm';

const ReportForm = () => {
  const [formData, setFormData] = useState({
    department: '',
    ReportName: {
      name: '',
      frequency: '',
      tables: [],
      Sources: [],
      Comments: ''
    },
    Alert_E_Mail: [],
    PowerBI_Refresh: {
      DatasetID: '',
      GroupdID: ''
    }
  });

  const departments = ['custservice', 'finance', 'operations', 'hr'];
  const [newEmail, setNewEmail] = useState('');

  const handleDepartmentChange = (e) => {
    setFormData({
      ...formData,
      department: e.target.value
    });
  };

  const handleReportNameChange = (e) => {
    setFormData({
      ...formData,
      ReportName: {
        ...formData.ReportName,
        name: e.target.value
      }
    });
  };

  const handleFrequencyChange = (e) => {
    setFormData({
      ...formData,
      ReportName: {
        ...formData.ReportName,
        frequency: e.target.value
      }
    });
  };

  const handlePowerBIChange = (field) => (e) => {
    setFormData({
      ...formData,
      PowerBI_Refresh: {
        ...formData.PowerBI_Refresh,
        [field]: e.target.value
      }
    });
  };

  const handleAddTable = () => {
    setFormData({
      ...formData,
      ReportName: {
        ...formData.ReportName,
        tables: [...formData.ReportName.tables, {
          tablename: '',
          query: [{
            queryType: 'SQL',
            QueryName: '',
            QueryOrder: '',
            QueryFileName: '',
            queryParameter: {}
          }],
          LoadType: 'FULL',
          WriteMode: 'APPEND',
          IsView: '0',
          View_Type: 'standalone'
        }]
      }
    });
  };

  const handleAddEmail = () => {
    if (newEmail && !formData.Alert_E_Mail.includes(newEmail)) {
      setFormData({
        ...formData,
        Alert_E_Mail: [...formData.Alert_E_Mail, newEmail]
      });
      setNewEmail('');
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log(JSON.stringify(formData, null, 2));
  };

  return (
    <Container className="mt-4">
      <Form onSubmit={handleSubmit}>
        <Row className="mb-3">
          <Col>
            <Form.Group>
              <Form.Label>Department</Form.Label>
              <Form.Select
                value={formData.department}
                onChange={handleDepartmentChange}
                required
              >
                <option value="">Select Department</option>
                {departments.map(dept => (
                  <option key={dept} value={dept}>{dept}</option>
                ))}
              </Form.Select>
            </Form.Group>
          </Col>
        </Row>

        <Row className="mb-3">
          <Col>
            <Form.Group>
              <Form.Label>Report Name</Form.Label>
              <Form.Control
                type="text"
                value={formData.ReportName.name}
                onChange={handleReportNameChange}
                required
              />
            </Form.Group>
          </Col>
          <Col>
            <Form.Group>
              <Form.Label>Frequency</Form.Label>
              <Form.Control
                type="text"
                value={formData.ReportName.frequency}
                onChange={handleFrequencyChange}
                required
              />
            </Form.Group>
          </Col>
        </Row>

        <div className="mb-3">
          <h4>Tables</h4>
          {formData.ReportName.tables.map((table, index) => (
            <TableForm
              key={index}
              table={table}
              index={index}
              formData={formData}
              setFormData={setFormData}
            />
          ))}
          <Button variant="secondary" onClick={handleAddTable} className="mt-2">
            Add Table
          </Button>
        </div>

        <div className="mb-3">
          <h4>PowerBI Refresh Settings</h4>
          <Row>
            <Col>
              <Form.Group>
                <Form.Label>Dataset ID</Form.Label>
                <Form.Control
                  type="text"
                  value={formData.PowerBI_Refresh.DatasetID}
                  onChange={handlePowerBIChange('DatasetID')}
                />
              </Form.Group>
            </Col>
            <Col>
              <Form.Group>
                <Form.Label>Group ID</Form.Label>
                <Form.Control
                  type="text"
                  value={formData.PowerBI_Refresh.GroupdID}
                  onChange={handlePowerBIChange('GroupdID')}
                />
              </Form.Group>
            </Col>
          </Row>
        </div>

        <div className="mb-3">
          <h4>Alert Emails</h4>
          <Row className="mb-2">
            <Col>
              <Form.Control
                type="email"
                value={newEmail}
                onChange={(e) => setNewEmail(e.target.value)}
                placeholder="Enter email"
              />
            </Col>
            <Col xs="auto">
              <Button variant="secondary" onClick={handleAddEmail}>
                Add Email
              </Button>
            </Col>
          </Row>
          <div>
            {formData.Alert_E_Mail.map((email, index) => (
              <div key={index} className="d-flex align-items-center mb-1">
                <span>{email}</span>
                <Button
                  variant="link"
                  className="text-danger"
                  onClick={() => {
                    const newEmails = formData.Alert_E_Mail.filter((_, i) => i !== index);
                    setFormData({ ...formData, Alert_E_Mail: newEmails });
                  }}
                >
                  Remove
                </Button>
              </div>
            ))}
          </div>
        </div>

        <Button variant="primary" type="submit">
          Generate JSON
        </Button>
      </Form>
    </Container>
  );
};

export default ReportForm;
