import React, { useState } from 'react';
import { Form, Button, Container, Row, Col, Card } from 'react-bootstrap';
import TableForm from './TableForm';

const ReportForm = ({ initialData, onBack }) => {
  const departments = [
    "custservice",
    "domesticops",
    "common",
    "domesticprod",
    "domnetint",
    "finance",
    "intlops",
    "intlprod",
    "it",
    "prodtech",
    "sales"
  ];

  const sourceTypes = ['TABLE', 'VIEW', 'MATERIALIZED_VIEW'];
  const loadTypes = ['FULL', 'INCREMENTAL'];
  const writeModes = ['APPEND', 'OVERWRITE', 'MERGE', 'PARTITION_OVERWRITE'];
  const viewTypes = ['materialized', 'standalone'];
  const isViewOptions = ['0', '1'];

  const [formData, setFormData] = useState(initialData || {
    department: '',
    ReportName: [{
      name: '',
      frequency: '',
      tables: [],
      Sources: [],
      Comments: '',
      Alert_E_Mail: [],
      PowerBI_Refresh: {
        DatasetID: '',
        GroupdID: ''
      }
    }]
  });

  const [newEmail, setNewEmail] = useState('');
  const [newSource, setNewSource] = useState('');
  const [newSourceType, setNewSourceType] = useState('');
  const [newSourceLoadType, setNewSourceLoadType] = useState('');
  const [newSourceWriteMode, setNewSourceWriteMode] = useState('');
  const [newSourceViewType, setNewSourceViewType] = useState('');
  const [newSourceIsView, setNewSourceIsView] = useState('');

  const handleDepartmentChange = (e) => {
    setFormData({
      ...formData,
      department: e.target.value
    });
  };

  const handleReportNameChange = (e) => {
    const newReportName = [...formData.ReportName];
    newReportName[0] = {
      ...newReportName[0],
      name: e.target.value
    };
    setFormData({
      ...formData,
      ReportName: newReportName
    });
  };

  const handleFrequencyChange = (e) => {
    const newReportName = [...formData.ReportName];
    newReportName[0] = {
      ...newReportName[0],
      frequency: e.target.value
    };
    setFormData({
      ...formData,
      ReportName: newReportName
    });
  };

  const handleCommentsChange = (e) => {
    const newReportName = [...formData.ReportName];
    newReportName[0] = {
      ...newReportName[0],
      Comments: e.target.value
    };
    setFormData({
      ...formData,
      ReportName: newReportName
    });
  };

  const handleAddTable = () => {
    const newReportName = [...formData.ReportName];
    newReportName[0].tables.push({
      tablename: '',
      query: [{
        queryType: 'SQL',
        QueryName: '',
        QueryOrder: '',
        QueryFileName: '',
        queryParameter: {}
      }],
      LoadType: 'FULL',
      WriteMode: 'APPEND',
      IsView: '0',
      View_Type: 'materialized'
    });
    setFormData({
      ...formData,
      ReportName: newReportName
    });
  };

  const handleAddSource = () => {
    if (newSource.trim() && newSourceType.trim() && newSourceLoadType.trim() && newSourceWriteMode.trim() && newSourceViewType.trim() && newSourceIsView.trim()) {
      const newReportName = [...formData.ReportName];
      newReportName[0].sources.push({
        source: newSource.trim(),
        sourceType: newSourceType.trim(),
        sourceLoadType: newSourceLoadType.trim(),
        sourceWriteMode: newSourceWriteMode.trim(),
        sourceViewType: newSourceViewType.trim(),
        sourceIsView: newSourceIsView.trim()
      });
      setFormData({ ...formData, ReportName: newReportName });
      setNewSource('');
      setNewSourceType('');
      setNewSourceLoadType('');
      setNewSourceWriteMode('');
      setNewSourceViewType('');
      setNewSourceIsView('');
    }
  };

  const handleAddEmail = () => {
    if (newEmail && !formData.ReportName[0].Alert_E_Mail.includes(newEmail)) {
      const newReportName = [...formData.ReportName];
      newReportName[0].Alert_E_Mail.push(newEmail);
      setFormData({
        ...formData,
        ReportName: newReportName
      });
      setNewEmail('');
    }
  };

  const handleRemoveSource = (sourceIndex) => {
    const newReportName = [...formData.ReportName];
    newReportName[0].sources = newReportName[0].sources.filter((_, i) => i !== sourceIndex);
    setFormData({ ...formData, ReportName: newReportName });
  };

  const handlePowerBIChange = (field) => (e) => {
    const newReportName = [...formData.ReportName];
    newReportName[0].PowerBI_Refresh[field] = e.target.value;
    setFormData({
      ...formData,
      ReportName: newReportName
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    
    // Create the JSON string
    const jsonString = JSON.stringify(formData, null, 2);
    
    // Create a blob with the JSON data
    const blob = new Blob([jsonString], { type: 'application/json' });
    
    // Create a URL for the blob
    const url = URL.createObjectURL(blob);
    
    // Create a temporary link element
    const link = document.createElement('a');
    link.href = url;
    link.download = `${formData.department}_${formData.ReportName[0].name}.json`;
    
    // Append the link to the document
    document.body.appendChild(link);
    
    // Trigger the download
    link.click();
    
    // Clean up
    document.body.removeChild(link);
    URL.revokeObjectURL(url);
  };

  return (
    <Container className="mt-4">
      <div className="dhl-form-container">
        <div className="d-flex justify-content-between align-items-center mb-4">
          <h2 className="mb-0">Report Configuration</h2>
          <Button 
            variant="secondary" 
            onClick={onBack}
            className="dhl-button"
          >
            Back to Options
          </Button>
        </div>
        <Form onSubmit={handleSubmit}>
          <Card className="mb-2">
            <Card.Body>
              <h4 className="dhl-section-title">Basic Information</h4>
              <Row className="mb-3">
                <Col md={4}>
                  <Form.Group>
                    <Form.Label className="fw-bold">Department</Form.Label>
                    <Form.Select
                      value={formData.department}
                      onChange={handleDepartmentChange}
                      required
                      className="dhl-select"
                    >
                      <option value="">Select Department</option>
                      {departments.map(dept => (
                        <option key={dept} value={dept}>{dept}</option>
                      ))}
                    </Form.Select>
                  </Form.Group>
                </Col>
                <Col md={4}>
                  <Form.Group>
                    <Form.Label className="fw-bold">Report Name</Form.Label>
                    <Form.Control
                      type="text"
                      value={formData.ReportName[0].name}
                      onChange={handleReportNameChange}
                      required
                      className="dhl-select"
                    />
                  </Form.Group>
                </Col>
                <Col md={4}>
                  <Form.Group>
                    <Form.Label className="fw-bold">Frequency</Form.Label>
                    <Form.Control
                      type="text"
                      value={formData.ReportName[0].frequency}
                      onChange={handleFrequencyChange}
                      className="dhl-select"
                    />
                  </Form.Group>
                </Col>
              </Row>
            </Card.Body>
          </Card>

          <div className="dhl-form-section">
            <h4>Tables</h4>
            {formData.ReportName[0].tables.map((table, index) => (
              <TableForm
                key={index}
                table={table}
                index={index}
                formData={formData}
                setFormData={setFormData}
              />
            ))}
            <div className="text-end">
              <Button variant="secondary" onClick={handleAddTable} className="dhl-button">
                Add Table
              </Button>
            </div>
          </div>

          <div className="dhl-form-section">
            <h4>Sources</h4>
            <div className="mb-3">
              <h5 className="mb-3">Sources</h5>
              <Row className="mb-2">
                <Col md={2}>
                  <Form.Group>
                    <Form.Label className="fw-bold">Source</Form.Label>
                    <Form.Control
                      type="text"
                      value={newSource}
                      onChange={(e) => setNewSource(e.target.value)}
                      placeholder="Enter source"
                      required
                      className="dhl-select"
                      style={{ height: '34px' }}
                    />
                  </Form.Group>
                </Col>
                <Col md={2}>
                  <Form.Group>
                    <Form.Label className="fw-bold">Source Type</Form.Label>
                    <Form.Select
                      value={newSourceType}
                      onChange={(e) => setNewSourceType(e.target.value)}
                      required
                      className="dhl-select"
                      style={{ height: '34px' }}
                    >
                      <option value="">Select Type</option>
                      {sourceTypes.map(type => (
                        <option key={type} value={type}>{type}</option>
                      ))}
                    </Form.Select>
                  </Form.Group>
                </Col>
                <Col md={2}>
                  <Form.Group>
                    <Form.Label className="fw-bold">Load Type</Form.Label>
                    <Form.Select
                      value={newSourceLoadType}
                      onChange={(e) => setNewSourceLoadType(e.target.value)}
                      required
                      className="dhl-select"
                      style={{ height: '34px' }}
                    >
                      <option value="">Select Load Type</option>
                      {loadTypes.map(type => (
                        <option key={type} value={type}>{type}</option>
                      ))}
                    </Form.Select>
                  </Form.Group>
                </Col>
                <Col md={2}>
                  <Form.Group>
                    <Form.Label className="fw-bold">Write Mode</Form.Label>
                    <Form.Select
                      value={newSourceWriteMode}
                      onChange={(e) => setNewSourceWriteMode(e.target.value)}
                      required
                      className="dhl-select"
                      style={{ height: '34px' }}
                    >
                      <option value="">Select Write Mode</option>
                      {writeModes.map(mode => (
                        <option key={mode} value={mode}>{mode}</option>
                      ))}
                    </Form.Select>
                  </Form.Group>
                </Col>
                <Col md={2}>
                  <Form.Group>
                    <Form.Label className="fw-bold">View Type</Form.Label>
                    <Form.Select
                      value={newSourceViewType}
                      onChange={(e) => setNewSourceViewType(e.target.value)}
                      required
                      className="dhl-select"
                      style={{ height: '34px' }}
                    >
                      <option value="">Select View Type</option>
                      {viewTypes.map(type => (
                        <option key={type} value={type}>{type}</option>
                      ))}
                    </Form.Select>
                  </Form.Group>
                </Col>
                <Col md={1}>
                  <Form.Group>
                    <Form.Label className="fw-bold">Is View</Form.Label>
                    <Form.Select
                      value={newSourceIsView}
                      onChange={(e) => setNewSourceIsView(e.target.value)}
                      required
                      className="dhl-select"
                      style={{ height: '34px' }}
                    >
                      <option value="">Select</option>
                      {isViewOptions.map(option => (
                        <option key={option} value={option}>{option}</option>
                      ))}
                    </Form.Select>
                  </Form.Group>
                </Col>
                <Col md={1} className="d-flex align-items-end">
                  <Button
                    variant="secondary"
                    onClick={handleAddSource}
                    className="dhl-button w-100"
                    disabled={!newSource.trim() || !newSourceType.trim() || !newSourceLoadType.trim() || !newSourceWriteMode.trim() || !newSourceViewType.trim() || !newSourceIsView.trim()}
                    style={{ height: '34px' }}
                  >
                    Add
                  </Button>
                </Col>
              </Row>
              {formData.ReportName[0].sources.map((source, sourceIndex) => (
                <div key={sourceIndex} className="d-flex align-items-center gap-2 mb-1">
                  <Col md={2}>
                    <Form.Control
                      type="text"
                      value={source.source}
                      disabled
                      className="dhl-select bg-light"
                      style={{ height: '34px' }}
                    />
                  </Col>
                  <Col md={2}>
                    <Form.Control
                      type="text"
                      value={source.sourceType}
                      disabled
                      className="dhl-select bg-light"
                      style={{ height: '34px' }}
                    />
                  </Col>
                  <Col md={2}>
                    <Form.Control
                      type="text"
                      value={source.sourceLoadType}
                      disabled
                      className="dhl-select bg-light"
                      style={{ height: '34px' }}
                    />
                  </Col>
                  <Col md={2}>
                    <Form.Control
                      type="text"
                      value={source.sourceWriteMode}
                      disabled
                      className="dhl-select bg-light"
                      style={{ height: '34px' }}
                    />
                  </Col>
                  <Col md={2}>
                    <Form.Control
                      type="text"
                      value={source.sourceViewType}
                      disabled
                      className="dhl-select bg-light"
                      style={{ height: '34px' }}
                    />
                  </Col>
                  <Col md={1}>
                    <Form.Control
                      type="text"
                      value={source.sourceIsView}
                      disabled
                      className="dhl-select bg-light"
                      style={{ height: '34px' }}
                    />
                  </Col>
                  <Col md={1}>
                    <Button
                      variant="secondary"
                      className="dhl-button w-100"
                      onClick={() => handleRemoveSource(sourceIndex)}
                      style={{ height: '34px' }}
                    >
                      Remove
                    </Button>
                  </Col>
                </div>
              ))}
            </div>
          </div>

          <div className="dhl-form-section">
            <h4>PowerBI Refresh Settings</h4>
            <Row>
              <Col md={6}>
                <Form.Group>
                  <Form.Label>Dataset ID</Form.Label>
                  <Form.Control
                    type="text"
                    value={formData.ReportName[0].PowerBI_Refresh.DatasetID}
                    onChange={handlePowerBIChange('DatasetID')}
                    className="dhl-input"
                  />
                </Form.Group>
              </Col>
              <Col md={6}>
                <Form.Group>
                  <Form.Label>Group ID</Form.Label>
                  <Form.Control
                    type="text"
                    value={formData.ReportName[0].PowerBI_Refresh.GroupdID}
                    onChange={handlePowerBIChange('GroupdID')}
                    className="dhl-input"
                  />
                </Form.Group>
              </Col>
            </Row>
          </div>

          <div className="dhl-form-section">
            <h4>Alert Emails</h4>
            <Row className="mb-2">
              <Col>
                <Form.Control
                  type="email"
                  value={newEmail}
                  onChange={(e) => setNewEmail(e.target.value)}
                  placeholder="Enter email"
                  className="dhl-input"
                />
              </Col>
              <Col xs="auto">
                <Button variant="secondary" onClick={handleAddEmail} className="dhl-button">
                  Add Email
                </Button>
              </Col>
            </Row>
            <div className="email-list">
              {formData.ReportName[0].Alert_E_Mail.map((email, index) => (
                <div key={index} className="email-item">
                  <span>{email}</span>
                  <Button
                    variant="link"
                    className="text-danger p-0"
                    onClick={() => {
                      const newReportName = [...formData.ReportName];
                      newReportName[0].Alert_E_Mail = newReportName[0].Alert_E_Mail.filter((_, i) => i !== index);
                      setFormData({ ...formData, ReportName: newReportName });
                    }}
                  >
                    Remove
                  </Button>
                </div>
              ))}
            </div>
          </div>

          <div className="text-center mt-4">
            <Button variant="primary" type="submit" className="dhl-button dhl-button-primary">
              Generate JSON
            </Button>
          </div>
        </Form>
      </div>
    </Container>
  );
};

export default ReportForm;
